mlex :: IO (Either String [Lexeme])
mlex = do 
    args <- getArgs
    case length args == 0 of
        True  -> do 
             putStrLn "Enter the Input Filename"
             s <- getLine
             contents <- readFile s
             abc <- return  (tokens contents)
             return abc
        False -> do
            let fname  = args !! 0 
            conts <- readFile fname
            etok <- return (tokens conts)
            return etok
                 
           
                   

